$(document).ready(function(){$("#edit").click(function(n){n.preventDefault();window.history.pushState(null,null,"/Movies/Edit/"+$("#edit_id").val());$("#details_movies").css("display","none");$("#edit_movies").css("display","block")});$("#details").click(function(n){n.preventDefault();window.history.pushState(null,null,"/Movies/Details/"+$("#edit_id").val());$("#details_movies").css("display","block");$("#edit_movies").css("display","none");$("#delete_movies").css("display","none")});$("#delete").click(function(n){n.preventDefault();window.history.pushState(null,null,"/Movies/Delete/"+$("#edit_id").val());$("#details_movies").css("display","none");$("#delete_movies").css("display","block")});$("#categoryId").change(function(n){n.preventDefault();$("#formCategorySubmit").submit()})});